$(function () {
    $(window).scroll(function() {
        if ($(window).scrollTop() > 100) {
            $("#YPRETURNTOP").fadeIn(500);
        }
        else
        {
            $("#YPRETURNTOP").fadeOut(500);
        }
    });
});

// 返回顶部
$("#YPRETURNTOP").on("click", function () {
    $("body,html").animate({
        scrollTop: 0
    }, 1000);
    return false;
});

/**
 * 轮播
 */
$("#carousel").carousel({
    interval: 10000
})

/**
 * 首页导航
 */
$(".menuitem").on("click", function() {
    var id  = $(this).attr("data-id");

    // 隐藏所有的
    $(".main-summary").find("div.markdown").addClass("hidden");

    // 显示当前点击的菜单
    var siblings    = $(".main-summary").find("div.markdown");
    $.each(siblings, function(key, item) {
        if ($(item).attr("data-id") == id)
        {
            $(item).removeClass("hidden").addClass("active");
            return ;
        }
    });
});
